import React, { Component } from 'react'

class ResponseForm extends Component {
    state = {
        wishes: [
            {id: 1, content: 'väska'},
            {id: 2, content: 'tröja'},
            {id: 3, content: 'trip'}
        ],
        content: ''
    }
    handleChange = (e) => {
        this.setState({
            content: e.target.value
        })
        console.log('CHANGED',this.state.content)
    }
    handleSubmit = (e) => {
        e.preventDefault()
        const singleWish = this.state.content
        const addWish = (wish) => {
            wish.id = Math.random()
            let newWish = [...this.state.wishes, wish]
            this.setState({
                wishes: newWish
            })
        }

        console.log('SUBMITTED', addWish(singleWish))
    }

    render() {
        const wishes = this.state.wishes.map(wish => {
            return(
                <li>{wish.content}</li>
            )
        })
        return (
            <div className="container">
                <form className="white" onSubmit={this.handleSubmit}>
                    <input onChange={this.handleChange} />
                </form>
                <ul>
                    {wishes}
                </ul>
            </div>
        )
    }
}

export default ResponseForm
