import React, { Component } from 'react'
import ResponseList from './ResponseList'

class ResponseForm extends Component {
    state = {
        content: '',
        wishes: [{id:1, text:'kl채nning'}, {id:2, text:'v채ska'}]
    }

    handleOnChange = (e) => {
        this.setState({
            content: e.target.value
        })
    }

    addWish = (wish) => {
        wish.id = Math.random();
        let newWish = [...this.state.wishes, wish]
        this.setState({
            wishes: newWish
        })

    }

    handleOnSubmit = (e) => {
        e.preventDefault()
        this.addWish(this.state.content)
    }

    render() {
        const { wishes } = this.state
        return(
            <div className="container">
                <form className="white" onSubmit={this.handleOnSubmit}>
                <h5 className="grey-text text-darken-3">Add your wishes</h5>
                <div className="input-field">
                    <label htmlFor="wish">Wish</label>
                    <input type="text" onChange={this.handleOnChange}/>
                </div>
                <div className="input-field">
                    <button className="btn pink lighten-1 z-depth-0">Add</button>
                </div>
                </form>
                <ResponseList wishes={wishes}/>
            </div>
        )
    }
}

export default ResponseForm
