import React, { Component } from 'react'
import ResponseListItem from './ResponseListItem'

class ResponseList extends Component {
    state = {
        content: '',
    }

    addWish = (wish) => {
        let id = Math.random()
        const newWish = [...this.state.wishes, wish]
        this.setState({
            wishes: newWish
        })
        console.log(this.state)
    }

    handleOnSubmit = (e) => {
        e.preventDefault()
        const addedWish = this.addWish(this.state)
        this.setState({
            content: addedWish
        })
    }

    render() {
        const { wishes } = this.props
        return(
            <div>
                <h5>Wish List</h5>
                <ul>
                    {wishes ? wishes.map(wish => {
                        return(
                            <ResponseListItem wish={wish} key={wish.id}/>
                        )
                    }) : null}
                </ul>
            </div>
        )
    }
}

export default ResponseList
